(541, 0, 541, NULL, NULL, 'PosgreSQL: Error when saving  ', '<p>When saving a menu item on Postgres, you receive a message like this:<br>\nJLIB_DATABASE_QUERY_FAILED SQL=SELECT * FROM e3xoe_menu WHERE "alias"<br>\n= ''article#1'' AND "parent_id" = ''1'' AND "client_id" = ''''<br>\nAND "language" = ''*''</p>\n\n<p>See:</p>\n\n<p><a href="http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&amp;tracker_item_id=29220">http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&amp;tracker_item_id=29220</a></p>', 3, 0, 1, '2012-11-10 00:47:27', '0000-00-00 00:00:00', '2012-11-10 00:47:27', 'https://github.com/joomla/joomla-cms/pull/541.diff'),
(542, 0, 542, 29567, NULL, ' [#29567] UTF8 SEF URLs are caucing browser too many redirects failures', '<p>This pull request addresses issue:<br>\n [#29567] UTF8 SEF URLs are caucing browser too many redirects failures</p>\n\n<p><a href="http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&amp;tracker_id=11410&amp;tracker_item_id=29567">http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&amp;tracker_id=11410&amp;tracker_item_id=29567</a></p>', 3, 0, 10, '2012-11-11 09:40:13', '2012-11-24 13:26:17', '2012-11-24 13:26:17', 'https://github.com/joomla/joomla-cms/pull/542.diff'),
(543, 0, 543, NULL, NULL, 'Update administrator/components/com_users/models/users.php', '<p>Fix for issue <a href="http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&amp;tracker_item_id=29596">http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&amp;tracker_item_id=29596</a>. Deleted redundant code.</p>', 3, 0, 1, '2012-11-12 17:00:05', '0000-00-00 00:00:00', '2012-11-12 17:00:05', 'https://github.com/joomla/joomla-cms/pull/543.diff'),
